import Header from './Header'
import Footer from './Footer'

const styleMain = {
    maxWidth: '900px'
}

const Layout = props => (
    <div>
        <Header/>
        <main style={styleMain}>
            {props.children}
        </main>
        <Footer/>
        <style jsx global>{`
            * {
                box-sizing: border-box;
            }
        
            body {
                margin: auto;
            }
        `}</style>
    </div>
)

export default Layout
