import React from "react";

const CardVertical = props => (
    <article>
      <div className="card">
        <img src={props.image} />
        <div className="content">
          <h4 className="depart">POLITIC</h4>
          <h4 className="title">{props.title}</h4>
        </div>
      </div>

      <style jsx>{`
        article {
          font-size: 30px;
        }

        .card {
          display: flex;
        }

        img {
          width: 30vw;
        }

        .content {
          padding-left: 8px;
        }

        h4 {
          margin: 0;
        }

        .depart {
          font-size: 0.25em;
          color: rgb(96, 96, 96);
          margin-bottom: 4px;
        }

        .title {
          font-size: 0.4em;
        }
      `}</style>
    </article>
)

export default CardVertical
