import Header from './Header'
import Footer from './Footer'

const styleMain = {
    maxWidth: '900px',
    margin: 'auto',
    display: 'table',
}

const Layout = props => (
    <main>
        <Header/>
        {props.children}
        <Footer/>

        <style jsx global>{`
            * {
                box-sizing: border-box;
            }
        
            body {
                margin: auto;
            }

            h1 {
                margin: 0;
            }

            main {
                margin: auto;
                
                display: table;
            }
        `}</style>
    </main>
)

export default Layout
