import Header from './Header'
import Footer from './Footer'

const styleMain = {
    maxWidth: '900px',
    margin: 'auto',
}

const Layout = props => (
    <main style={styleMain}>
        <Header/>
        {props.children}
        <Footer/>

        <style jsx global>{`
            * {
                box-sizing: border-box;
            }
        
            body {
                margin: auto;
            }

            h1 {
                margin: 0;
            }
        `}</style>
    </main>
)

export default Layout
