import React from "react";

export default function CardVertical(props) {
  const imgNothing =
    "https://images.unsplash.com/photo-1532003885409-ed84d334f6cc?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1050&q=80";

  const styleCard = {
    display: "flex",
    height: "15vh"
  };

  const styleImage = {
    width: "30%",
    backgroundImage: `url(${props.image || imgNothing})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    marginRight: "10px"
  };

  const styleContainer = {
    width: "70%",
    textAlign: "left"
  };

  const styleDepart = {
    margin: "0 0 8px 0",
    fontSize: "0.65em",
    color: "rgb(96,96,96)"
  };

  const styleTitle = {
    margin: 0,
    fontSize: "0.8em"
  };

  return (
    <article>
      <div style={styleCard}>
        <div style={styleImage} />
        <div style={styleContainer}>
          <h4 style={styleDepart}>POLITIC</h4>
          <h4 style={styleTitle}>{props.title}</h4>
        </div>
      </div>
    </article>
  );
}
